DELIMITER $$
CREATE TRIGGER `trg_rating_product_delete` AFTER DELETE ON `comment` FOR EACH ROW BEGIN
    if OLD.type = 'PRODUCT' then

        UPDATE product
        SET product.rating        =
                (SELECT ROUND(COALESCE(AVG(rating), 0), 1)
                 FROM comment
                 where comment.productId = OLD.productId
                   and comment.status in ('APPROVED', 'REPLY')),
            product.countFeedBack = (SELECT COALESCE(COUNT(*), 0)
                                     FROM comment
                                     where comment.productId = OLD.productId
                                       and comment.status in ('APPROVED', 'REPLY'))
        where id = OLD.productId;

    else

        UPDATE enterprise
        SET enterprise.rating        =
                (SELECT ROUND(COALESCE(AVG(rating), 0), 1)
                 FROM comment
                 where comment.enterPriseId = OLD.enterPriseId
                   and comment.status in ('APPROVED', 'REPLY')),
